request.get().then((result) => {
    // returned data will be accessible on `result.data` 
    period = result.data[0]['periods'][0];
    //console.log(JSON.stringify(period));

    switch (period.cloudsCoded) {
    case "CL":
        msg1.payload = "clear";
        msg2.payload = 0;
        msg3.payload = 1;      
        break;
     case "FW":
        msg1.payload = "mostly clear";
        msg2.payload = 10;
        msg3.payload = 1;      
        break;
     case "SC":
        msg1.payload = "partly cloudy";
        msg2.payload = 50;
        msg3.payload = 2;      
        break;
    case "BK":
        msg1.payload = "mostly cloudy";
        msg2.payload = 75;
        msg3.payload = 2;      
        break;
    case "OV":
        msg1.payload = "overcast";
        msg2.payload = 100;
        msg3.payload = 3;      
        break;
    default:
        console.log("Unknown cloud code: " + period.cloudsCoded);
        msg1.payload = "uknoown";
        msg2.payload = 100;
        msg3.payload = 3;
    }
    node.send([msg1, msg2, msg3]);
    node.done();
});

