const api = new AmbientWeatherApi({
  apiKey: env.get("ASTRODASH_AMBIENT_WEATHER_API_KEY"),
  applicationKey: env.get("ASTRODASH_AMBIENT_WEATHER_APPLICATION_KEY")
})

api.userDevices().then((devices) => {

  devices.forEach((device) => {
    // fetch the most recent data
    api.deviceData(device.macAddress, {
      limit: 2880
    })
    .then((deviceData) => {

        temp_series = [];
        dewpoint_series = [];      
        humidity_series = [];
        windspeed_series = [];
        windgust_series = [];
        winddir_series = [];
        barom_series = [];

        deviceData.forEach((data) => {

            d = Date.parse(data.date);

            tempc = (data.tempf - 32) * 5 / 9;
            temp_series.unshift({x: d, y: tempc});
            dewpointc = (data.dewPoint - 32) * 5 / 9;
            dewpoint_series.unshift({x: d, y: dewpointc});

            //dt = {x: d, y: data.humidity};
            //console.log(JSON.stringify(dt, null, 4)); 
            humidity_series.unshift({x: d, y: data.humidity});

            windspeed = data.windspeedmph * 1.609344;
            windspeed_series.unshift({x: d, y: windspeed});
            //windspeed_series.push({});

            windgust = data.windgustmph * 1.609344;
            windgust_series.unshift({x: d, y: windgust});
            //windgust_series.push({});

            winddir_series.unshift({x: d, y: data.winddir});
            winddir_series.unshift({});

            barom = data.baromrelin * 33.864;
            barom_series.unshift({x: d, y: barom});

        })

        temp_msg = {};
        hum_msg = {};
        windspeed_msg = {};
        winddir_msg = {};
        barom_msg = {};

        temp_msg.payload = [{
            "series": ["Temperature", "Dew Point"],
            "data": [
                temp_series,
                dewpoint_series
                ],
            "labels": ["Temp", "Dew Point"]
        }];   

        hum_msg.payload = [{
            "series": ["Humidity"],
            "data": [
                humidity_series
                ],
            "labels": ["Humidity"]
        }];   

       windspeed_msg.payload = [{
            "series": ["Wind Speed", "Gust"],
            "data": [
                windspeed_series,
                windgust_series
                ],
            "labels": ["Wind Speed", "Gust"]
        }];   

        winddir_msg.payload = [{
            "series": ["Wind Direction"],
            "data": [
                winddir_series
                ],
            "labels": ["Wind Direction"]
        }]; 

        barom_msg.payload = [{
            "series": ["Pressure"],
            "data": [
                barom_series
                ],
            "labels": ["Pressure"]
        }]; 

        node.send([temp_msg, hum_msg, windspeed_msg, winddir_msg, barom_msg]);
        node.done();
    })
  })
})


